// A modest frame checker...

// The first condition below is the one that triggers because the magic cookie
// in the message header is not correct.  But the message length prefix and the
// the number of bytes received (the message body) seems to always be correct. 

static int iMsgSmellsValid(InternodeMsg_t *iMsg, int bytesReceived){
   int dump = MAX(bytesReceived, internode_msg_size(iMsg));
   if (iMsg->header.magic != INM_MAGIC) {
       logWarn("CLIENT RX #%d: Discarding invalid internode message. Framing error.", rxCount);
       hexDump(lmInternode, "CLIENT RX: iMSG:", iMsg, dump);
       return 0;
   }
   if (iMsg->header.msgType <= 0 || iMsg->header.msgType >= MSG_MAX_TYPES) {
       logWarn("CLIENT RX #%d: Discarding invalid internode message. Invalid msgType %s", rxCount, iMsg->header.msgType);
       hexDump(lmInternode, "CLIENT RX: iMSG:", iMsg, dump);
       return 0;
   }
   if (bytesReceived < (int) sizeof(((InternodeMsg_t*)0)->header)) {
       logWarn("CLIENT RX: Discarding invalid internode message. Framing error, insufficient bytes received");
       return 0;
   }

   return 1;
}


